import React, { useState } from 'react';
import styles from './styles.module.css';
import Modal from 'react-modal';

interface Props {
  movie: Movie;
  url: string;
}

interface Movie {
  Poster?: string;
  Title?: string;
  Type?: string;
  Year?: string;
  imdbID?: string;
  Rated?: string;
  Released?: string,
  Runtime?: string,
  Genre?: string,
  Director?: string,
  Writer?: string,
  Actors?: string[],
  Plot?: string,
  Language?: string,
  Country?: string,
}

Modal.setAppElement('#root');

const MovieCard:React.FC<Props> = ({movie, url}) => {
  const [modalIsOpen, setIsOpen] = useState(false);
  const [details, setDetails] = useState<Movie>({});

  async function fetchMovie() {
    try {
      const results = await fetch(`${url}&i=${movie.imdbID}`);
      const body = await results.json();

      setDetails(body);
    } catch(err) {
      console.error(err);
    }
  }

  function handleOnClick() {
    fetchMovie()
    setIsOpen(true)
  }
  console.log(details)

  return (
    <>
    <div className={styles.card} onClick={handleOnClick}>
      <div className={styles.title}>{movie.Title}</div>
      <div className={styles.year}>{movie.Year}</div>
      <img src={movie.Poster} />
    </div>
    <Modal isOpen={modalIsOpen} id='modal' className={styles.modal}>
      <ul className={styles.details}>
        <li>Title: {details.Title}</li>
        <li>Year: {details.Year}</li>
        <li>Rated: {details.Rated}</li>
        <li>Released: {details.Released}</li>
        <li>Runtime: {details.Runtime}</li>
        <li>Genre: {details.Genre}</li>
        <li>Director: {details.Director}</li>
        <li>Writer: {details.Writer}</li>
        <li>Actors: {details.Actors}</li>
        <li>Plot: {details.Plot}</li>
        <li>Language: {details.Language}</li>
        <li>Country: {details.Country}</li>
        <li>Poster: {details.Poster}</li>
      </ul>
    </Modal>
    </>
  )
}

export default MovieCard;
