import React, { useState } from 'react';
import styles from './styles.module.css';
import Modal from 'react-modal';

interface Props {
  movie: {
    Poster: string;
    Title: string;
    Type: string;
    Year: string;
    imdbID: string;
  }
  url: string;
}

interface MovieDetails extends Props {
  Released: string,
  Runtime: string,
  Genre: string,
  Director: string,
  Writer: string,
  Actors: string[],
  Plot: string,
  Language: string,
  Country: string,
}

Modal.setAppElement('#root');

const MovieCard:React.FC<Props> = ({movie, url}) => {
  const [modalIsOpen, setIsOpen] = useState(false);
  const [details, setDetails] = useState<MovieDetails>();

  async function fetchMovie() {
    try {
      const results = await fetch(`${url}&i=${movie.imdbID}`);
      const body = await results.json();

      setDetails(body);
    } catch(err) {
      console.error(err);
    }
  }

  function handleOnClick() {
    fetchMovie()
    setIsOpen(true)
  }

  return (
    <>
    <div className={styles.card} onClick={handleOnClick}>
      <div className={styles.title}>{movie.Title}</div>
      <div className={styles.year}>{movie.Year}</div>
      <img src={movie.Poster} />
    </div>
    <Modal isOpen={modalIsOpen} id='modal' className={styles.modal}>
      <div className={styles.title}>{details.Title}</div>
      <div className={styles.year}>{details.Year}</div>
    </Modal>
    </>
  )
}

export default MovieCard;
